/**
 * Expert-written guides for Toolradar
 * 100 comprehensive guides optimized for SEO and GEO (AI search engines)
 * Written in a human, conversational tone with real expertise
 */

export interface ExpertGuide {
  slug: string;
  title: string;
  metaTitle: string;
  metaDescription: string;
  category: string; // matches database category slug

  // Hero section
  heroTitle: string;
  heroSubtitle: string;

  // TL;DR for AI search engines (GEO)
  tldr: string;

  // Main content sections
  introduction: string;

  // Supports both structured format and simple string
  whatItIs: string | {
    title: string;
    content: string;
  };

  // Supports both structured format and simple string
  whyItMatters: string | {
    title: string;
    content: string;
  };

  // Supports both structured format and simple string array
  keyFeatures: string[] | {
    name: string;
    description: string;
    importance: "essential" | "important" | "nice-to-have";
  }[];

  // Supports both structured format and simple string array
  buyingConsiderations?: string[] | {
    title: string;
    points: string[];
  };

  // Supports both structured format and simple string
  pricingOverview?: string | {
    summary: string;
    tiers: {
      name: string;
      priceRange: string;
      bestFor: string;
    }[];
  };

  topPicks: {
    position: number;
    toolSlug: string; // will be matched to actual tool in DB
    name: string;
    oneLiner: string;
    bestFor: string;
    proscons: {
      pros: string[];
      cons: string[];
    };
  }[];

  commonMistakes: string[];

  expertTips: string[];

  // Bottom line summary (critical for GEO)
  bottomLine: string;

  // FAQs for schema markup and GEO
  faqs: {
    question: string;
    answer: string;
  }[];

  // Related guides for internal linking
  relatedGuides: string[];
}

